import '../css/Animation.css'
import React, { useEffect, useRef, useState } from 'react';
import ReactLive2d from "./ReactLive2d"


function Animation(props){
    const [previousSave, newSave] = useState('')
    const audioElement = new Audio
    if (props.token !== previousSave){
        newSave(props.token)
    }

    useEffect( ()=> {
        console.log(previousSave)
        var request = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({'delete': false, 'token': previousSave})
        }

        if (previousSave !== ''){
            const wavFile = fetch('/audioBeg', request)
            .then(file => file.arrayBuffer())
            .then(arrayBuffer => Blob(arrayBuffer, {type: "audio/wav"}))

            audioElement.src = URL.createObjectURL(wavFile)

            audioElement.play()
        }
    }, [previousSave])



    return <div class = "AnimationBox"><ReactLive2d
    width={1200}
    height={1200}
    top = {2}></ReactLive2d> </div>
}  
export default Animation

