import '../css/Animation.css'
import React, { useEffect, useRef } from 'react';
import {Live2DCubismFramework} from './Framework/src/model/cubismmodel'


function Animation(props){
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const builder = new Li();

        async function loadModel() {
        const modelJsonPath = './Resources/Mao/Mao.model3.json'; // Path to your Live2D model's JSON file
        const idleAnimationName = 'Idle'; // Name of the idle animation

        // Load model data
        await builder.loadModel(modelJsonPath);

        // Create model instance
        const model = builder.buildModel();

        // Load idle animation
        await model.motionManager.loadMotion(modelJsonPath, idleAnimationName);

        // Start idle animation
        model.motionManager.startMotion(idleAnimationName);

        // Set up rendering context
        const options = {
            canvas: canvas,
            alpha: true,
            antialias: true,
        };

        const gl = canvas.getContext('webgl', options) || canvas.getContext('experimental-webgl', options);

        // Initialize and start rendering
        model.initialize(gl);
        function renderLoop() {
            model.update();
            model.draw();
            requestAnimationFrame(renderLoop);
        }
        renderLoop();
        }

        loadModel();

        return () => {
        // Clean up resources
        builder.release();
        };
    }, []);

    return <canvas ref={canvasRef} />;
  
}  
export default Animation

