  }

  /**
   * モーションデータをグループ名から一括でロードする。
   * モーションデータの名前は内部でModelSettingから取得する。
   * 从组合名称中统一加载动作数据。
   * 动作数据的名称在内部从ModelSetting取得。
   *
   * @param group モーションデータのグループ名
   * group动作数据的组名称
   */
  preLoadMotionGroup(group) {
    for (let i = 0; i < this._modelSetting.getMotionCount(group); i++) {
      const motionFileName = this._modelSetting.getMotionFileName(group, i);
      // ex) idle_0
      const name = `${group}_${i}`;
      if (this._debugMode) {
        LAppPal.printMessage(`[APP]load motion: ${motionFileName} => [${name}]`);
      }
      fetch(`${this._modelHomeDir}${motionFileName}`).then(response => response.arrayBuffer()).then(arrayBuffer => {
        const tmpMotion = this.loadMotion(arrayBuffer, arrayBuffer.byteLength, name);
        let fadeTime = this._modelSetting.getMotionFadeInTimeValue(group, i);
        if (fadeTime >= 0.0) {
          tmpMotion.setFadeInTime(fadeTime);
        }
        fadeTime = this._modelSetting.getMotionFadeOutTimeValue(group, i);
        if (fadeTime >= 0.0) {
          tmpMotion.setFadeOutTime(fadeTime);
        }
        tmpMotion.setEffectIds(this._eyeBlinkIds, this._lipSyncIds);
        if (this._motions.getValue(name) != null) {
          ACubismMotion.delete(this._motions.getValue(name));
        }
        this._motions.setValue(name, tmpMotion);
        this._motionCount++;
        // console.log('this._motionCount',this._motionCount)
        // console.log('this._allMotionCount',this._allMotionCount)
        if (this._motionCount >= this._allMotionCount) {
          this._state = LoadStep.LoadTexture;

          // 全てのモーションを停止する
          document.getElementById('live2d-hidden').style.display = 'none';
          if (this._motionManager) {
            this._motionManager.stopAllMotions();
            this.createRenderer();
            this.setupTextures();
            this.getRenderer().startUp(gl);
          }
          this._updating = false;
          this._initialized = true;

          // this.createRenderer();
          // this.setupTextures();
          // this.getRenderer().startUp(gl);
          document.getElementById('live2d').style.visibility = 'visible';
        }
      });
    }
  }

  /**
   * すべてのモーションデータを解放する。
   */
  releaseMotions() {
    this._motions.clear();
  }

  /**
   * 全ての表情データを解放する。
   */
  releaseExpressions() {
    this._expressions.clear();
  }

  /**
   * モデルを描画する処理。モデルを描画する空間のView-Projection行列を渡す。
   */
  doDraw() {
    if (this._model == null) return;

    // キャンバスサイズを渡す
    const viewport = [0, 0, canvas.width, canvas.height];
    this.getRenderer().setRenderState(frameBuffer, viewport);
    this.getRenderer().drawModel();
  }

  /**
   * モデルを描画する処理。モデルを描画する空間のView-Projection行列を渡す。
   */
  draw(matrix) {
    if (this._model == null) {
      return;
    }

    // 各読み込み終了後
    if (this._state == LoadStep.CompleteSetup) {
      matrix.multiplyByMatrix(this._modelMatrix);
      this.getRenderer().setMvpMatrix(matrix);
      this.doDraw();
    }
  }

  /**
   * コンストラクタ
   */
  constructor() {
    super();
    this._modelSetting = void 0;
    // モデルセッティング情報
    this._modelHomeDir = void 0;
    // モデルセッティングが置かれたディレクトリ
    this._userTimeSeconds = void 0;
    // デルタ時間の積算値[秒]
    this._eyeBlinkIds = void 0;
    // モデルに設定された瞬き機能用パラメータID
    this._lipSyncIds = void 0;
    // モデルに設定されたリップシンク機能用パラメータID
    this._motions = void 0;
    // 読み込まれているモーションのリスト
    this._expressions = void 0;
    // 読み込まれている表情のリスト
    this._hitArea = void 0;
    this._userArea = void 0;
    this._idParamAngleX = void 0;
    // パラメータID: ParamAngleX
    this._idParamAngleY = void 0;
    // パラメータID: ParamAngleY
    this._idParamAngleZ = void 0;
    // パラメータID: ParamAngleZ
    this._idParamEyeBallX = void 0;
    // パラメータID: ParamEyeBallX
    this._idParamEyeBallY = void 0;
    // パラメータID: ParamEyeBAllY
    this._idParamBodyAngleX = void 0;
    // パラメータID: ParamBodyAngleX
    this._state = void 0;
    // 現在のステータス管理用
    this._expressionCount = void 0;
    // 表情データカウント
    this._textureCount = void 0;
    // テクスチャカウント
    this._motionCount = void 0;
    // モーションデータカウント
    this._allMotionCount = void 0;
    this._modelSetting = null;
    this._modelHomeDir = null;
    this._userTimeSeconds = 0.0;
    this._eyeBlinkIds = new csmVector();
    this._lipSyncIds = new csmVector();
    this._motions = new csmMap();
    this._expressions = new csmMap();
    this._hitArea = new csmVector();
    this._userArea = new csmVector();
    this._idParamAngleX = CubismFramework.getIdManager().getId(CubismDefaultParameterId.ParamAngleX);
    this._idParamAngleY = CubismFramework.getIdManager().getId(CubismDefaultParameterId.ParamAngleY);
    this._idParamAngleZ = CubismFramework.getIdManager().getId(CubismDefaultParameterId.ParamAngleZ);
    this._idParamEyeBallX = CubismFramework.getIdManager().getId(CubismDefaultParameterId.ParamEyeBallX);
    this._idParamEyeBallY = CubismFramework.getIdManager().getId(CubismDefaultParameterId.ParamEyeBallY);
    this._idParamBodyAngleX = CubismFramework.getIdManager().getId(CubismDefaultParameterId.ParamBodyAngleX);
    this._state = LoadStep.LoadAssets;
    this._expressionCount = 0;
    this._textureCount = 0;
    this._motionCount = 0;
    this._allMotionCount = 0;
  }

  // モーション総数
}

