export function strtod(s: string, endPtr: string[]): number {
  let index = 0;
  for (let i = 1; ; i++) {
    const testC: string = s.slice(i - 1, i);

    // 
    if (testC == 'e' || testC == '-' || testC == 'E') {
      continue;
    } // 

    const test: string = s.substring(0, i);
    const number = Number(test);
    if (isNaN(number)) {
      // 
      break;
    } // index

    index = i;
  }
  let d = parseFloat(s); // 

  if (isNaN(d)) {
    // 
    d = NaN;
  }

  endPtr[0] = s.slice(index); // 
  return d;
}

