import { CubismMatrix44 } from '../math/cubismmatrix44';
import { CubismModel } from '../model/cubismmodel';

/**
 * 
 *
 * 
 */
export abstract class CubismRenderer {
  /**
   * 
   *
   * @return 
   */
  public static create(): CubismRenderer {
    return null;
  }

  /**
   * 
   */
  public static delete(renderer: CubismRenderer): void {
    renderer = null;
  }

  /**
   * 
   * 
   * @param model 
   */
  public initialize(model: CubismModel): void {
    this._model = model;
  }

  /**
   * 
   */
  public drawModel(): void {
    if (this.getModel() == null) return;

    this.saveProfile();

    this.doDrawModel();

    this.restoreProfile();
  }

  /**
   * Model-View-Projection 
   * 
   * @param matrix44 Model-View-Projection 
   */
  public setMvpMatrix(matrix44: CubismMatrix44): void {
    this._mvpMatrix4x4.setMatrix(matrix44.getArray());
  }

  /**
   * Model-View-Projection 
   * @return Model-View-Projection 
   */
  public getMvpMatrix(): CubismMatrix44 {
    return this._mvpMatrix4x4;
  }

  /**
   * 
   * 0.0~1.01.0
   * @param red 
   * @param green 
   * @param blue 
   * @param alpha 
   */
  public setModelColor(
    red: number,
    green: number,
    blue: number,
    alpha: number
  ): void {
    if (red < 0.0) {
      red = 0.0;
    } else if (red > 1.0) {
      red = 1.0;
    }

    if (green < 0.0) {
      green = 0.0;
    } else if (green > 1.0) {
      green = 1.0;
    }

    if (blue < 0.0) {
      blue = 0.0;
    } else if (blue > 1.0) {
      blue = 1.0;
    }

    if (alpha < 0.0) {
      alpha = 0.0;
    } else if (alpha > 1.0) {
      alpha = 1.0;
    }

    this._modelColor.R = red;
    this._modelColor.G = green;
    this._modelColor.B = blue;
    this._modelColor.A = alpha;
  }

