import { LAppDelegate } from '../src/lappdelegate';
import React, { useState, useEffect } from 'react';

function ReactLive2d(props) {

    // 容器样式
    let containerStyle = {
        position: 'fixed',
        top: props.top ? props.top : '0',
        bottom: props.bottom ? props.bottom : '0',
        left: `calc(60% - ${props.width})`
    }
    // canvas样式
    let canvasStyle = {
        position: 'relative',
        top: props.top ? props.top : '',
        right: props.right ? props.right : '0',
        bottom: props.bottom ? props.bottom : '0',
        left: props.left ? props.left : ''
    }

    useEffect(() => {
        if (!navigator.userAgent.match(/mobile/i) || props.MobileShow == true) {
            console.log('hiwewe')
            if (LAppDelegate.getInstance().initialize() == false) {
                return;
            }
            LAppDelegate.getInstance().run();
            // window.onbeforeunload = () => LAppDelegate.releaseInstance();
        }
    }, []);

    const [previousSave, newSave] = useState('')
    const audioElement = new Audio
    if (props.token !== previousSave){
        newSave(props.token)
    }

    useEffect( ()=> {
        console.log(previousSave)
        var request = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({'delete': true, 'token': previousSave})
        }

        if (previousSave !== ''){
            fetch('/audioBeg', request)
            .then(file => file.arrayBuffer())
            .then(arrayBuffer => {
                const wavFile = new Blob( [arrayBuffer], {type: "audio/wav"})
                audioElement.src = URL.createObjectURL(wavFile)
                audioElement.play()
                LAppDelegate.getInstance().testing(previousSave)
            }
            )

        }
    }, [previousSave])



    useEffect(() =>{

        if(props.release==true){
            LAppDelegate.releaseInstance();
        }
    }, [props.release])

    return (
        <div>
            <div
                style={containerStyle}
                width={props.width ? props.width : '300'}
                height={props.height ? props.height : '500'}
                id="live2d-container">
                <div id="live2d-hidden"
                    style={{
                        width:'100%',
                        height:'100%',
                        position:'absolute',
                        top:'0',
                        left:'0',
                        zIndex:'2'
                    }}
                >

                </div>
                <canvas
                    id="live2d"
                    style={canvasStyle}
                    width={props.width ? props.width : '300'}
                    height={props.height ? props.height : '500'}
                    className="live2d"
                >
                </canvas>
            </div>
        </div>
    )
}

export default ReactLive2d
