import '../css/Chat.css'
import {useState, useEffect, useRef} from 'react';
import PopUp from './Popup';
import DOMPurify from 'dompurify';


function ChatMessage(props){
    const className = props.user ? 'chatLogUser': 'chatLogAssistant'
    
    let boxText = props.realChild
    let requiersReadMore = false


    const processLength = (child) => {
        if (displayedText.length < 200){
            requiersReadMore = true
            return displayedText.substring(0, 200) 
        }
    } 

    const processEnter = (child) => {
        child = DOMPurify.sanitize(child)
        console.log(child)
        return <h3 dangerouslySetInnerHTML={{__html: child.replace(/\n/g, '<br />')}}></h3>
    }

    let displayedText = props.realChild

    const checkReadMore = (child) => {
        if (requiersReadMore){
            return <div class = 'readMore' style={{backgroundColor: 'cyan'}}>...</div>
        }
        return ''
    }


    return (<div class = {className}><h3>{displayedText}{checkReadMore()}</h3></div>)
}


export default ChatMessage
